mapper.update:
UPDATE MEMBER SET
MEMBER_PW = #{memberPw}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_EMAIL = #{memberEmail},
MEMBER_NICKNAME = #{memberNickname}

	, MEMBER_PROFILE = #{memberProfile}

WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}
