mapper.update:
UPDATE MEMBER SET
MEMBER_PW
= #{memberPw}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_EMAIL = #{memberEmail},
MEMBER_NICKNAME =
#{memberNickname}

	, MEMBER_PROFILE = #{memberProfile}

WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET MEMBER_PW = #{memberPw}
WHERE
MEMBER_EMAIL = #{memberEmail}

UPDATE RESERVE SET
RESERVE_STATUS = 'N'
WHERE RESERVE_NO = #{reserveNo}

mapper.select:
SELECT CLASS_CONTENT ,MEMBER_NO FROM CLASS_PAGE WHERE
CLASS_NO =
#{classNo}

SELECT COUNT(*) XXX FROM DUAL
WHERE (SELECT COUNT(*) FROM PAYMENT
WHERE MEMBER_NO = #{memberNo}
AND CLASS_NO = #{classNo}
AND PAYMENT_STATUS = 'Y'
AND PAYMENT_DT < SYSDATE) > 0

AND (SELECT COUNT(*) FROM REVIEW
WHERE MEMBER_NO = #{memberNo}
AND CLASS_NO = #{classNo}
AND REVIEW_STATUS = 'Y') = 0
