mapper.select:
		SELECT COUNT(*) CNT FROM CLASS_PAGE WHERE CLASS_TYPE_NO = #{classType}

		
			AND

			

			

				
					CLASS_TITLE LIKE #{val}
				
				
					CLASS_CONTENT LIKE #{val}
				
				
					( CLASS_TITLE LIKE #{val} OR CLASS_CONTENT LIKE
					#{val} )
				
				
					MEMBER_NICKNAME LIKE #{val}

SELECT CLASS_NO,CATEGORY_NAME, CLASS_TITLE,MEMBER_NICKNAME,MEMBER_PROFILE FROM CLASS_PAGE LEFT JOIN CATEGORY USING(CATEGORY_NO) LEFT JOIN MEMBER USING(MEMBER_NO) WHERE CLASS_TYPE_NO =#{classType}
AND
CLASS_TITLE LIKE #{val}
CLASS_CONTENT LIKE #{val}
( CLASS_TITLE LIKE #{val} OR CLASS_CONTENT LIKE #{val} )
MEMBER_NICKNAME LIKE #{val}
AND CLASS_STATUS = 'Y' ORDER BY CLASS_NO DESC

SELECT RESERVE_START,RESERVE_END, (SELECT COUNT(*) FROM
CONFIRM_RESERVE B WHERE B.RESERVE_NO = A.RESERVE_NO) XXX
FROM RESERVE A
WHERE CLASS_NO = #{classNo}
AND RESERVE_DT = #{reserveDate}
AND RESERVE_STATUS= 'Y'

mapper.insert:
INSERT INTO ATTACHMENT SELECT SEQ_FNO.NEXTVAL, A.* FROM (
SELECT #{at.filePath} FILE_PATH, #{at.fileName} FILE_NAME, #{at.fileLevel} FILE_LEVEL, #{at.classNo} CLASS_NO FROM DUAL
) A

mapper.select:
		SELECT COUNT(*) XXX FROM DUAL 
WHERE (SELECT COUNT(*) FROM PAYMENT 
        WHERE MEMBER_NO = #{memberNo}
        AND CLASS_NO = #{classNo}
        AND PAYMENT_STATUS = 'Y'
        AND PAYMENT_DT < SYSDATE) > 0
        
AND   (SELECT COUNT(*) FROM REVIEW
            WHERE MEMBER_NO = #{memberNo}
