mapper.select:
SELECT * FROM REVIEW_ATTACHMENT
WHERE REVIEW_NO = ${reviewNo}
ORDER BY FILE_LEVEL

mapper.insert:
INSERT INTO REVIEW_ATTACHMENT SELECT SEQ_REVIEWFNO.NEXTVAL, A.* FROM (
SELECT #{at.filePath} FILE_PATH, #{at.fileName} FILE_NAME, #{at.fileLevel} FILE_LEVEL, #{at.reviewNo} REVIEW_NO FROM DUAL
) A

mapper.update:
UPDATE REVIEW SET
REVIEW_CONTENT = #{reviewContent}
WHERE REVIEW_NO = #{reviewNo}

UPDATE REVIEW SET
REVIEW_STATUS = 'N'
WHERE REVIEW_NO = #{reviewNo}
