mapper.update:
UPDATE CLASS_PAGE SET
CLASS_READ_COUNT = CLASS_READ_COUNT + 1
WHERE CLASS_NO = #{classNo}

mapper.insert:
INSERT INTO ATTACHMENT SELECT SEQ_FNO.NEXTVAL, A.* FROM (
SELECT #{at.filePath} FILE_PATH, #{at.fileName} FILE_NAME, #{at.fileLevel} FILE_LEVEL, #{at.classNo} CLASS_NO FROM DUAL
) A

mapper.update:
UPDATE CLASS_PAGE SET
CLASS_TITLE = #{classTitle},
CLASS_CONTENT = #{classContent},
CATEGORY_NO = #{categoryNo}
WHERE CLASS_NO = #{classNo}

mapper.delete:
DELETE FROM ATTACHMENT
WHERE CLASS_NO = #{classNo}
AND FILE_LEVEL IN ( ${deleteVideos} )

mapper.update:
UPDATE ATTACHMENT
SET
FILE_NM = #{fileName}
WHERE CLASS_NO = #{classNo}
AND FILE_LEVEL = #{fileLevel}

UPDATE CLASS_PAGE SET
CLASS_TITLE = #{classTitle},
CLASS_CONTENT = #{classContent},
CATEGORY_NO = #{categoryNo}
WHERE CLASS_NO = #{classNo}

UPDATE CLASS_PAGE SET
CLASS_STATUS = 'N'
WHERE CLASS_NO = #{classNo}

mapper.select:
SELECT * FROM ONLINE_PAYMENT
WHERE MEMBER_NO = #{memberNo}
AND CLASS_NO = #{classNo}
AND PAYMENT_STATUS = 'Y'
